
import { useState, useEffect } from 'react';
import './Dishes.css';
import choosen from './DishesImage/choosen.png';
import AOS from 'aos';
import 'aos/dist/aos.css';



const DishCards = [
    { id: 1, name: 'Cesear', price: 15, img: 'https://www.seriouseats.com/thmb/Fi_FEyVa3_-_uzfXh6OdLrzal2M=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/the-best-caesar-salad-recipe-06-40e70f549ba2489db09355abd62f79a9.jpg' },
    { id: 2, name: 'Carbonara', price: 60, img: 'https://static01.nyt.com/images/2021/02/14/dining/carbonara-horizontal/carbonara-horizontal-master768-v2.jpg' },
    { id: 3, name: 'English Breakfast', price: 120, img: 'https://s3-eu-west-1.amazonaws.com/s3-gousto-production-media/cms/mood-image/3301_2-All-Day-English-Breakfast-With-Chips--Beans-9361-1616494956723.jpg' },
    { id: 4, name: 'Toad in Hole', price: 55, img: 'https://www.daringgourmet.com/wp-content/uploads/2021/09/Toad-in-the-Hole-6.jpg' },
    { id: 5, name: 'Fish and Chips', price: 43, img: 'https://forkandtwist.com/wp-content/uploads/2021/04/IMG_0102-500x500.jpg' },
    { id: 6, name: ' Kidney Pie', price: 43, img: 'https://thegoodcompanions.ca/wp-content/uploads/2021/05/Steak-and-Kidney-Premium-Pie_1000x750.jpg' },
    { id: 7, name: 'Coq au vin', price: 49, img: 'data:image/jpeg;base64,' },
    { id: 8, name: 'Cassaulet', price: 67, img: 'data:image/jpeg;base64,' },
    { id: 9, name: 'Bouillabaisse', price: 43, img: 'data:image/jpeg;base64,' },

];

const Dishes = ({ addToCart }) => {
    const [filter, setFilter] = useState('');
    const [value, setValue] = useState(Array(DishCards.length).fill(1));
    const [selectedOptions, setSelectedOptions] = useState(Array(DishCards.length).fill({ size: '', quantity: 0 }));



    useEffect(() => {
        AOS.init({ duration: 2000 });
    }, []);

    const FilterDish = (e) => {
        setFilter(e.target.value);
    };

    const BtnClick = (size, cardIndex) => {
        setSelectedOptions((prevSelectedOptions) => {
            const updatedSelectedOptions = [...prevSelectedOptions];
            updatedSelectedOptions[cardIndex] = { ...updatedSelectedOptions[cardIndex], size };
            return updatedSelectedOptions;
        });
    };



    const Add = (index) => {
        setValue((prevValue) => {
            const updatedValue = [...prevValue];
            updatedValue[index] += 1;
            return updatedValue;
        });
    };

    const Minus = (index) => {
        setValue((prevValue) => {
            const updatedValue = [...prevValue];
            updatedValue[index] = Math.max(updatedValue[index] - 1, 0);
            return updatedValue;
        });
    };



    const filteredCards = DishCards.filter((dishcard) => {
        if (filter === 'All') {
            return true;
        } else if (filter === '30') {
            return dishcard.price < 30;
        } else if (filter === '50') {
            return dishcard.price > 50;
        } else if (filter === '100') {
            return dishcard.price > 100;
        } else {
            return true;
        }
    });


    return (
        <div className='Dishes'>
            <select value={filter} onChange={FilterDish}>
                <option value="All">All</option>
                <option value="30">Less 30</option>
                <option value="50">Higher 50</option>
                <option value="100">Higher 100</option>
            </select>
            <div className="CardsContainer">
                {filteredCards.map((dishcard, cardIndex) => (
                    <div key={dishcard.id} className='Card'
                        data-aos='fade-right'>
                        <div className="CardImage">
                            <img src={dishcard.img} alt="" className='CardImg' />
                        </div>
                        <div className="CardText">
                            <h3 className='CardName' data-aos='fade-up'>{dishcard.name}</h3>
                            <p className='Price' data-aos='fade-up'>Price: ${dishcard.price}</p>
                            <div className="CardPortion PortionDrinks">
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'small' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('small', cardIndex)}
                                >
                                    Small
                                </button>
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'standard' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('standard', cardIndex)}
                                >
                                    Standard
                                </button>
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'bigger' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('bigger', cardIndex)}
                                >
                                    Bigger
                                </button>
                            </div>


                            <div className="CardCount">
                                <div
                                    className='CardCountBtn'
                                    onClick={() => Minus(cardIndex)}
                                >
                                    -
                                </div>
                                <input
                                    type="text"
                                    className="CardCountText"
                                    value={value[cardIndex]}
                                    onChange={(e) => setValue((prevValue) => {
                                        const updatedValue = [...prevValue];
                                        updatedValue[cardIndex] = parseInt(e.target.value) || 0;
                                        return updatedValue;
                                    })}
                                />
                                <div
                                    className='CardCountBtn plus'
                                    onClick={() => Add(cardIndex)}
                                >
                                    +
                                </div>
                            </div>



                        </div>
                        <button
                            className='add'
                            onClick={(e) => {
                                e.stopPropagation();
                                addToCart(dishcard, selectedOptions[cardIndex], value[cardIndex]);
                            }}
                        >
                            <span>A</span>dd
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Dishes;
