import './Drinks.css';
import { useState } from 'react';
import { useEffect } from 'react';

import AOS from 'aos'
import 'aos/dist/aos.css'
const Cards = [
    { id: 1, name: 'Coca Cola', price: '15', img: 'data:image/jpeg;base64,' },
    { id: 2, name: 'Mojito', price: '17', img: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Orange juice', price: '16', img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRcWeWlWkNiHvyO-v74o2oMx2rTdtLps6Oxqw&usqp=CAU' },
    { id: 4, name: 'Virgin Mojito.', price: '13', img: 'data:image/jpeg;base64,' },
    { id: 5, name: 'Roy Roger', price: '25', img: 'https://cf.ltkcdn.net/cocktails/images/orig/269874-1600x1030-roy-rogers-drinks-sweet-down-earth-recipes.jpg' },
    { id: 6, name: 'Pina Colada', price: '27', img: 'data:image/jpeg;base64,' },
    { id: 7, name: 'Sherley Temple', price: '22', img: 'https://bellyfull.net/wp-content/uploads/2020/12/Shirley-Temple-Drink-blog.jpg' },
    { id: 8, name: 'Sunrise Mocktail', price: '33', img: 'data:image/jpeg;base64,' },
    { id: 9, name: 'Boston Cooler', price: '16', img: 'https://www.allrecipes.com/thmb/CXv3NJuozlzVip-ZylzXiLEr09w=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/GettyImages-1178861826-2000-3b8ad2920e9a4caabf90f72575b56936.jpg' },


];

const Drinks = ({ addToCart }) => {

    useEffect(() => {
        AOS.init({ duration: 2000 })
    }, [])





    const [value, setValue] = useState(Array(Cards.length).fill(1));
    const [selectedOptions, setSelectedOptions] = useState(Array(Cards.length).fill({ size: '', quantity: 0 }));


    const BtnClick = (size, cardIndex) => {
        setSelectedOptions((prevSelectedOptions) => {
            const updatedSelectedOptions = [...prevSelectedOptions];
            updatedSelectedOptions[cardIndex] = { ...updatedSelectedOptions[cardIndex], size };
            return updatedSelectedOptions;
        });
    };



    const Add = (index) => {
        setValue((prevValue) => {
            const updatedValue = [...prevValue];
            updatedValue[index] += 1;
            return updatedValue;
        });
    };

    const Minus = (index) => {
        setValue((prevValue) => {
            const updatedValue = [...prevValue];
            updatedValue[index] = Math.max(updatedValue[index] - 1, 0);
            return updatedValue;
        });
    };





    return (

        <div className="Drinks">
            <h3 className='Main__text'><span>D</span>rinks</h3>
            <div className="CardsContainer">
                {Cards.map((dishcard, cardIndex) => (
                    <div key={dishcard.id} className='Card' data-aos='fade-right'>
                        <div className="CardImage">

                            <img src={dishcard.img} alt="" className='CardImg' />
                        </div>
                        <div className="CardText">
                            <h3 className='CardName' data-aos='fade-left'>{dishcard.name}</h3>


                            <p className='Price'>Price: <span>{dishcard.price}$</span></p>



                            <div className="CardPortion PortionDrinks">
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'small' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('small', cardIndex)}
                                >
                                    Small
                                </button>
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'standard' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('standard', cardIndex)}
                                >
                                    Standard
                                </button>
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'bigger' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('bigger', cardIndex)}
                                >
                                    Bigger
                                </button>
                            </div>

                            <div className="CardCount">
                                <div
                                    className='CardCountBtn'
                                    onClick={() => Minus(cardIndex)}
                                >
                                    -
                                </div>
                                <input
                                    type="text"
                                    className="CardCountText"
                                    value={value[cardIndex]}
                                    onChange={(e) => setValue((prevValue) => {
                                        const updatedValue = [...prevValue];
                                        updatedValue[cardIndex] = parseInt(e.target.value) || 0;
                                        return updatedValue;
                                    })}
                                />
                                <div
                                    className='CardCountBtn plus'
                                    onClick={() => Add(cardIndex)}
                                >
                                    +
                                </div>
                            </div>
                            <button
                                className='add'
                                onClick={(e) => {
                                    e.stopPropagation();
                                    addToCart(dishcard, selectedOptions[cardIndex], value[cardIndex]);
                                }}
                            >
                                <span>A</span>dd
                            </button>



                        </div>



                    </div>
                ))}
            </div>

        </div>
    )
}

export default Drinks;