import './Snacks.css';
import { useState } from 'react';

import { useEffect } from 'react';
import AOS from 'aos'
import 'aos/dist/aos.css'

const Snacks = ({ addToCart }) => {

    const Cards = [
        { id: 1, name: 'French fries', price: 25, img: 'data:image/jpeg;base64,' },
        { id: 2, name: 'Nuggets', price: 38, img: 'data:image/jpeg;base64,' },
        { id: 3, name: 'Bruschetta', price: 43, img: 'https://res.cloudinary.com/usmed-com/images/f_auto,fl_lossy,q_auto:eco,g_auto:subject/f_auto,q_auto:eco/v1595540588/bruscetta/bruscetta.jpg?_i=AA' },
        { id: 4, name: 'Nachous', price: 35, img: 'https://www.seriouseats.com/thmb/YBUAG17xy1nWYGPmFcJKeoODTzk=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/cheese-sauce-for-cheese-fries-and-nachos-hero-01-e6ccf966688c43ec8025cf9a19678423.jpg' },
        { id: 5, name: 'Quesadilla', price: 29, img: 'data:image/jpeg;base64,' },
        { id: 6, name: 'Jeon', price: 37, img: 'data:image/jpeg;base64,' }
        , { id: 7, name: 'Falafel', price: 26, img: "https://i2.wp.com/www.downshiftology.com/wp-content/uploads/2019/07/Falafel-7.jpg" },
        { id: 8, name: 'Shaobing', price: 28, img: "https://cookinginchinglish.com/wp-content/uploads/2020/11/scallion-shaobing_-13.jpg" },
        { id: 9, name: 'Corokke', price: 36, img: 'https://cdn.apartmenttherapy.info/image/upload/f_jpg,q_auto:eco,c_fill,g_auto,w_1500,ar_1:1/k%2FPhoto%2FRecipe%20Ramp%20Up%2F2022-02-Korokke%2F01_KOROKKE' }
    ];

    useEffect(() => {
        AOS.init({ duration: 2000 })
    }, [])


    const [value, setValue] = useState(Array(Cards.length).fill(1));
    const [selectedOptions, setSelectedOptions] = useState(Array(Cards.length).fill({ size: '', quantity: 0 }));


    const BtnClick = (size, cardIndex) => {
        setSelectedOptions((prevSelectedOptions) => {
            const updatedSelectedOptions = [...prevSelectedOptions];
            updatedSelectedOptions[cardIndex] = { ...updatedSelectedOptions[cardIndex], size };
            return updatedSelectedOptions;
        });
    };



    const Add = (index) => {
        setValue((prevValue) => {
            const updatedValue = [...prevValue];
            updatedValue[index] += 1;
            return updatedValue;
        });
    };

    const Minus = (index) => {
        setValue((prevValue) => {
            const updatedValue = [...prevValue];
            updatedValue[index] = Math.max(updatedValue[index] - 1, 0);
            return updatedValue;
        });
    };



    return (
        <div className="Snacks">
            <h3 className='Main__text'><span>S</span>nacks</h3>
            <div className="CardsContainer">
                {Cards.map((dishcard, cardIndex) => (
                    <div key={dishcard.id} className='Card' data-aos='fade-right'>
                        <div className="CardImage">

                            <img src={dishcard.img} alt="" className='CardImg' />
                        </div>
                        <div className="CardText">
                            <h3 className='CardName' data-aos='fade-down'>{dishcard.name}</h3>


                            <p className='Price'>Price: <span>{dishcard.price}$</span></p>
                            <div className="CardPortion PortionDrinks">
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'small' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('small', cardIndex)}
                                >
                                    Small
                                </button>
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'standard' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('standard', cardIndex)}
                                >
                                    Standard
                                </button>
                                <button
                                    className={selectedOptions[cardIndex]?.size === 'bigger' ? 'PortionBtnRed' : 'PortionBtn'}
                                    onClick={() => BtnClick('bigger', cardIndex)}
                                >
                                    Bigger
                                </button>
                            </div>


                            <div className="CardCount">
                                <div
                                    className='CardCountBtn'
                                    onClick={() => Minus(cardIndex)}
                                >
                                    -
                                </div>
                                <input
                                    type="text"
                                    className="CardCountText"
                                    value={value[cardIndex]}
                                    onChange={(e) => setValue((prevValue) => {
                                        const updatedValue = [...prevValue];
                                        updatedValue[cardIndex] = parseInt(e.target.value) || 0;
                                        return updatedValue;
                                    })}
                                />
                                <div
                                    className='CardCountBtn plus'
                                    onClick={() => Add(cardIndex)}
                                >
                                    +
                                </div>
                            </div>



                        </div>
                        <button
                            className='add'
                            onClick={(e) => {
                                e.stopPropagation();
                                addToCart(dishcard, selectedOptions[cardIndex], value[cardIndex]);
                            }}
                        >
                            <span>A</span>dd
                        </button>








                    </div>
                ))}
            </div>
        </div >
    )
}
export default Snacks;